import { useState } from 'react';
import './ExpenseForm.css'

const ExpenseForm = (props) => {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');
    const [formDisplay, setFormDisplay] = useState(false)

    const inputChangeHandler = (identifier, value) => {
        if (identifier === 'title') {
            setEnteredTitle(value);
        } else if (identifier === 'amount') {
            setEnteredAmount(value);
        } else {
            setEnteredDate(value);
        }
    }

    const submitHandler = (e) => {
        e.preventDefault();
        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        }

        props.onSaveExpenseData(expenseData)

        setEnteredTitle('');
        setEnteredAmount('');
        setEnteredDate('');
    }

    const displayForm = () => {
        setFormDisplay(!formDisplay)
    }

    if (!formDisplay) {
        return <button onClick={displayForm}>Add new Expense</button>
    }
    return (
        <form onSubmit={submitHandler}>
            <div className='new-expense__controls'>
                <div className='new-expense__controls'>
                    <label>Title</label>
                    <input type='text' value={enteredTitle} onChange={(e) => inputChangeHandler('title', e.target.value)} />
                </div>
                <div className='new-expense__controls'>
                    <label>Amount</label>
                    <input type='number' min='0.01' step='0.01' value={enteredAmount} onChange={(e) => inputChangeHandler('amount', e.target.value)} />
                </div>
                <div className='new-expense__controls'>
                    <label>Date</label>
                    <input type='date' min='2019-01-01' max='2023-12-31' value={enteredDate} onChange={(e) => inputChangeHandler('date', e.target.value)} />
                </div>
            </div>
            <div className='new-expense__actions'>
                <button type='button' onClick={displayForm}>Cancel</button>
                <button type='submit' >Add Expense</button>
            </div>
        </form>
    );
}

export default ExpenseForm;
